import React, { useLayoutEffect, useRef}from "react";
import './App.css'
import cycling from './Images/double cycling.gif';
import sun from './Images/sun.png';
import tree from './Images/tree.png';
import cloud from './Images/cloud.png';
import house from './Images/house.png';
import polelight from './Images/polelight.png';
import bush from './Images/bush.png';
//  import useWebAnimations from '@wellyshen/use-web-animations'
function App() {
      const cloudMove = useRef(null)

      useLayoutEffect(() => {
        var cloudFrames = [
          {transform: 'translate(1200px, 0)'},
          {transform: 'translate(-1400px, 0)'},
        ];
        var cloud = cloudMove.current.animate(
          cloudFrames, {

            iteration: Infinity,
            duration: 1500,
            delay: 500,

          }
        )
      }) 

  return (
    <div className="container">

       <div className="sky">
         <div className="sky-item">
       <img src={cloud} alt=" " className="cloud" ref={cloudMove} /> 
       <div className="house-tree">
       <img src={house} alt="" className="house"/>
       </div>
       <img src={sun} alt="" className="sun" />
       <div className="house-tree">

       <img src={tree} alt=" " className="tree" />
      </div>
       <img src={cloud} alt=" " className="cloud" ref={cloudMove} /> 

       </div> 

    </div>


      <div className="road">
      <img src={polelight} alt=" " className="polelight" />




      <img src={bush} alt=" " className="bush" />
      <img src={cycling} alt=" "className="cycling"/>





      <img src={tree} alt=" " className="tree2" />


      </div>
      <div className="Floor1">

      </div>

     </div>

  );
}

export default App;
