import React from "react";
import './App.css'
import useWebAnimations from '@wellyshen/use-web-animations'
function App() {

  const { ref, playState, getAnimation} = useWebAnimations({
    keyframes: {
      transform: ["translateX( 800px)"],      //Move by 500px
      background:["orange", "brown"],    //Go through three colors
    },
    timing: {
      delay: 500,          //start with a 500ms delay
      duration: 1000,      // Run for 1000ms
      iterations: Infinity,       //Repear once
      direction: "alternate",   //Run the animation forward then backward
      easing: "ease-in-out",    //Use a fancy timing function
    },
  })

  return (
    <div>
     <div className='target' ref={ref}>
     <h1>Pakistan Zindabaad</h1>
     </div> <br />
     <h1>Current animation state: {playState}</h1>
     <button onClick={() => getAnimation().pause()}>Pause</button>
    </div>
  );
}

export default App;
